import React, {Component} from 'react';
import Panel from 'react-bootstrap/lib/Panel'
import axios from 'axios'
import './App.css';

//This Component is a child Component of Customers Component
export default class CustomerDetails extends Component {

  constructor(props) {
    super(props);
    this.state = {}
  }

  //Function which is called when the component loads for the first time
  componentDidMount() {
    this.getCustomerDetails(this.props.val)
  }

  //Function which is called whenver the component is updated
  componentDidUpdate(prevProps) {

    //get Customer Details only if props has changed
    if (this.props.val !== prevProps.val) {
      this.getCustomerDetails(this.props.val)
    }
  }

  //Function to Load the customerdetails data from json.
  getCustomerDetails(id) {
    axios.get('assets/samplejson/customer' + id + '.json').then(response => {
      this.setState({customerDetails: response})
    })
  };

  render() {
    if (!this.state.customerDetails)
      return (<p>Loading Data</p>)
    return (<div className="count"><div className="customerdetails">
      <Panel bsStyle="info" className="leftalign">
        <Panel.Heading>
          <Panel.Title componentClass="h3">{'National Count'}</Panel.Title>
        </Panel.Heading>
        <Panel.Body className="cases">
          <p>Active Cases : {this.state.customerDetails.data.active}</p>
          <p>Discharged : {this.state.customerDetails.data.discharged}</p>
          <p>Deaths : {this.state.customerDetails.data.deaths}</p>
          <p> Total Vaccination : {this.state.customerDetails.data.vaccination}</p>
        </Panel.Body>
      </Panel>
    </div>
    </div>)
  }
}
