import React, { useState } from "react";
import India from "@svg-maps/india";
import { SVGMap } from "react-svg-map";
import "react-svg-map/lib/index.css";
import axios from 'axios';

function IndiaMap(){
  const [stateCode, setStateCode] = useState("");
  const [stateName, setStateName] = useState("");
  const [i, seti] = useState("");


  function onLocationClick(event) {
    setStateCode(event.target.id);
    setStateName(event.target.getAttribute("name"));
    // console.log(event.target);
    console.log("Id", event.target.id);
    console.log("Name",stateName);
    var str="";
    axios.get('assets/samplejson/customer1.json').then(response => {
      str=response;
      var i=str.indexOf(stateName);
      seti(i);
    })
  }

  return (
    <>
      <SVGMap map={India} onLocationClick={onLocationClick} />
      <p>{stateName}</p>
      <p>{stateCode}</p>
      <p>i</p>
    </>
  );
}

export default IndiaMap;
